import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Properties from './pages/Properties';
import Sell from './pages/Sell';
import HomeLoans from './pages/HomeLoans';
import Agents from './pages/Agents';
import Blog from './pages/Blog';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Sidebar from './components/Sidebar';
import './App.css';

const sampleProperties = [
  {
    id: 1,
    title: "Luxury Apartment in South Mumbai",
    price: "₹2,50,00,000",
    address: "Malabar Hill, Mumbai, Maharashtra",
    beds: 3,
    baths: 3,
    area: 2200,
    location: "Mumbai",
    type: "Apartment",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1613977257363-707ba9348227?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  },
  {
    id: 2,
    title: "Modern 2 BHK in Delhi",
    price: "₹85,00,000",
    address: "Saket, New Delhi",
    beds: 2,
    baths: 2,
    area: 1200,
    location: "Delhi",
    type: "Apartment",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1545324418-cc1a3fa10c00?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  },
  {
    id: 3,
    title: "Villa in Bangalore",
    price: "₹1,20,00,000",
    address: "Whitefield, Bangalore, Karnataka",
    beds: 4,
    baths: 3,
    area: 3200,
    location: "Bangalore",
    type: "Villa",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1448630360428-65456885c650?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1167&q=80"
  },
  {
    id: 4,
    title: "3 BHK Apartment for Rent in Hyderabad",
    price: "₹35,000/month",
    address: "Gachibowli, Hyderabad, Telangana",
    beds: 3,
    baths: 2,
    area: 1500,
    location: "Hyderabad",
    type: "Apartment",
    transaction: "Rent",
    image: "https://images.unsplash.com/photo-1574362848149-11496d93a7c7?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=784&q=80"
  },
  {
    id: 5,
    title: "2 BHK in Chennai",
    price: "₹65,00,000",
    address: "Adyar, Chennai, Tamil Nadu",
    beds: 2,
    baths: 2,
    area: 1100,
    location: "Chennai",
    type: "Apartment",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  },
  {
    id: 6,
    title: "Farmhouse in Pune",
    price: "₹1,50,00,000",
    address: "Lavasa, Pune, Maharashtra",
    beds: 5,
    baths: 4,
    area: 4500,
    location: "Pune",
    type: "Villa",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1605146769289-440113cc3d00?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  },
  {
    id: 7,
    title: "1 BHK for Rent in Kolkata",
    price: "₹18,000/month",
    address: "Salt Lake City, Kolkata, West Bengal",
    beds: 1,
    baths: 1,
    area: 650,
    location: "Kolkata",
    type: "Apartment",
    transaction: "Rent",
    image: "https://images.unsplash.com/photo-1522708323590-d24dbb6b0267?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  },
  {
    id: 8,
    title: "Commercial Space in Ahmedabad",
    price: "₹2,00,00,000",
    address: "Prahlad Nagar, Ahmedabad, Gujarat",
    beds: 0,
    baths: 2,
    area: 2500,
    location: "Ahmedabad",
    type: "Commercial",
    transaction: "Sale",
    image: "https://images.unsplash.com/photo-1497366811353-6870744d04b2?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
  }
];

function App() {
  const [showForm, setShowForm] = useState(false);
  const [filters, setFilters] = useState({
    location: '',
    priceRange: '',
    propertyType: '',
    transactionType: '',
    bedrooms: ''
  });
  const [showFilters, setShowFilters] = useState(false);
  const [activeTab, setActiveTab] = useState('buy');
  
  // Refs for section scrolling
  const buySectionRef = useRef(null);
  const rentSectionRef = useRef(null);
  const sellSectionRef = useRef(null);
  const projectsSectionRef = useRef(null);
  const agentsSectionRef = useRef(null);
  const contactSectionRef = useRef(null);
  const blogtSectionRef = useRef(null);

  // Filter properties based on selected filters
  const filteredProperties = sampleProperties.filter(property => {
    // Filter by transaction type (Buy/Rent)
    if (activeTab === 'buy' && property.transaction !== 'Sale') return false;
    if (activeTab === 'rent' && property.transaction !== 'Rent') return false;
    
    // Other filters
    if (filters.location && property.location !== filters.location) return false;
    if (filters.propertyType && property.type !== filters.propertyType) return false;
    if (filters.bedrooms && property.beds !== parseInt(filters.bedrooms)) return false;
    
    // Price filter logic
    if (filters.priceRange) {
      if (filters.priceRange === '0-50' && property.transaction === 'Sale') {
        const price = parseInt(property.price.replace(/[^0-9]/g, ''));
        if (price > 5000000) return false;
      }
      // Add more price range conditions as needed
    }
    
    return true;
  });

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  const clearFilters = () => {
    setFilters({
      location: '',
      priceRange: '',
      propertyType: '',
      transactionType: '',
      bedrooms: ''
    });
  };

  // Function to scroll to specific section
  const scrollToSection = (sectionRef) => {
    if (sectionRef && sectionRef.current) {
      sectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Navigation handler
  const handleNavigation = (section) => {
    switch(section) {
      case 'buy':
        setActiveTab('buy');
        setShowForm(false);
        setTimeout(() => scrollToSection(buySectionRef), 100);
        break;
      case 'rent':
        setActiveTab('rent');
        setShowForm(false);
        setTimeout(() => scrollToSection(rentSectionRef), 100);
        break;
      case 'sell':
        setActiveTab('sell');
        setShowForm(true);
        setTimeout(() => scrollToSection(sellSectionRef), 100);
        break;
      case 'projects':
        setShowForm(false);
        setTimeout(() => scrollToSection(projectsSectionRef), 100);
        break;
      case 'agents':
        setShowForm(false);
        setTimeout(() => scrollToSection(agentsSectionRef), 100);
        break;
      case 'contact':
        setShowForm(false);
        setTimeout(() => scrollToSection(contactSectionRef), 100);
        break;
      case 'Blog':
        setShowForm(false);
        setTimeout(() => scrollToSection(blogtSectionRef), 100);
        break;
      default:
        break;
    }
  };

  return (
    <div className="App">
      <Header onToggleForm={() => setShowForm(!showForm)} showForm={showForm} onNavigate={handleNavigation} />
      
      <div className="main-container">
        <div className="content-column">
          {!showForm ? (
            <>
              <section className="hero">
                <div className="hero-content">
                  <h1>Find Your Dream Property</h1>
                  <p>Discover the perfect home from our curated collection of premium properties</p>
                  
                  <div className="transaction-tabs">
                    <button 
                      className={activeTab === 'buy' ? 'active' : ''}
                      onClick={() => {
                        setActiveTab('buy');
                        scrollToSection(buySectionRef);
                      }}
                    >
                      <i className="fas fa-shopping-cart"></i> Buy
                    </button>
                    <button 
                      className={activeTab === 'rent' ? 'active' : ''}
                      onClick={() => {
                        setActiveTab('rent');
                        scrollToSection(rentSectionRef);
                      }}
                    >
                      <i className="fas fa-key"></i> Rent
                    </button>
                    <button 
                      className={activeTab === 'sell' ? 'active' : ''}
                      onClick={() => {
                        setActiveTab('sell');
                        setShowForm(true);
                        scrollToSection(sellSectionRef);
                      }}
                    >
                      <i className="fas fa-blog"></i> Rent
                    </button>
                    <button 
                      className={activeTab === 'Blog' ? 'active' : ''}
                      onClick={() => {
                        setActiveTab('Blog');
                        setShowForm(true);
                        scrollToSection(sellSectionRef);
                      }}
                    >
                      <i className="fas fa-tag"></i> Sell
                    </button>
                  </div>
                  
                  <div className="search-bar">
                    <input type="text" placeholder="Search by location, property type, etc..." />
                    <button><i className="fas fa-search"></i> Search</button>
                  </div>
                  <button 
                    className="filter-toggle"
                    onClick={() => setShowFilters(!showFilters)}
                  >
                    <i className={`fas ${showFilters ? 'fa-times' : 'fa-filter'}`}></i>
                    {showFilters ? 'Hide Filters' : 'Show Filters'}
                  </button>
                </div>
              </section>
              
              {/* Buy Properties Section */}
              {(activeTab === 'buy' || activeTab === 'rent') && (
                <>
                  <div ref={activeTab === 'buy' ? buySectionRef : rentSectionRef}></div>
                  {showFilters && (
                    <FilterSection 
                      filters={filters} 
                      onFilterChange={handleFilterChange}
                      onClearFilters={clearFilters}
                    />
                  )}
                  
                  <section className="container">
                    <h2 className="section-title">
                      {filteredProperties.length} Properties Found for {activeTab === 'buy' ? 'Sale' : 'Rent'}
                    </h2>
                    <div className="property-grid">
                      {filteredProperties.map(property => (
                        <PropertyCard key={property.id} property={property} />
                      ))}
                    </div>
                    {filteredProperties.length === 0 && (
                      <div className="no-properties">
                        <i className="fas fa-home fa-3x"></i>
                        <h3>No properties match your filters</h3>
                        <p>Try adjusting your filters or clear them to see more properties</p>
                        <button className="clear-filters-btn" onClick={clearFilters}>
                          Clear All Filters
                        </button>
                      </div>
                    )}
                  </section>
                </>
              )}
              
              {/* Top Agents Section */}
              <div ref={agentsSectionRef}></div>
              <TopAgents locationFilter={filters.location} />
              
              {/* New Projects Section */}
              <div ref={projectsSectionRef}></div>
              <NewProjects locationFilter={filters.location} />
            </>
          ) : (
            <>
              <div ref={sellSectionRef}></div>
              <FormSection />
            </>
          )}
        </div>
        
        <Sidebar />
      </div>
      
      {/* Contact Section */}
      <div ref={contactSectionRef}></div>
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-column">
            <h3>About PropertyList</h3>
            <p>PropertyList is a leading real estate marketplace dedicated to helping people find their perfect home.</p>
            <div className="social-icons">
              <a href="#"><i className="fab fa-facebook-f"></i></a>
              <a href="#"><i className="fab fa-twitter"></i></a>
              <a href="#"><i className="fab fa-instagram"></i></a>
              <a href="#"><i className="fab fa-linkedin-in"></i></a>
            </div>
          </div>
          
          <div className="footer-column">
            <h3>Quick Links</h3>
            <a href="#" onClick={() => handleNavigation('buy')}>Buy Property</a>
            <a href="#" onClick={() => handleNavigation('rent')}>Rent Property</a>
            <a href="#" onClick={() => handleNavigation('sell')}>Sell Property</a>
            <a href="#" onClick={() => handleNavigation('projects')}>New Projects</a>
            <a href="#" onClick={() => handleNavigation('agents')}>Top Agents</a>
          </div>
          
          <div className="footer-column">
            <h3>Popular Locations</h3>
            <a href="#">Mumbai</a>
            <a href="#">Delhi</a>
            <a href="#">Bangalore</a>
            <a href="#">Hyderabad</a>
            <a href="#">Chennai</a>
          </div>
          
          <div className="footer-column">
            <h3>Contact Us</h3>
            <p><i className="fas fa-map-marker-alt"></i> 123 Property Street, Mumbai, India</p>
            <p><i className="fas fa-phone"></i> +91 9876543210</p>
            <p><i className="fas fa-envelope"></i> info@propertylist.com</p>
          </div>
        </div>
        
        <div className="copyright">
          <p>&copy; 2023 PropertyList. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
